'use strict';
var evolutionWallet = require('../lib/wallet');

describe('evolutionWallet', () => {
    const Wallet = new evolutionWallet();

    describe('constructor', () => {
        it('should have default host set to `127.0.0.1`', () => {
            new evolutionWallet().hostname.should.equal('127.0.0.1');
        });

        it('should have default port set to 55055', () => {
            new evolutionWallet().port.should.equal(55055);
        });
    });

    describe('methods', () => {
        describe('balance()', () => {
            it('should retrieve the account balance', (done) => {
                Wallet.balance().then(function(result){
                    result.balance.should.be.a.Number();
                    done();
                })
            })
        })

        describe('address()', () => {
            it('should return the account address', (done) => {
                Wallet.address().then(function(result){
                    result.address.should.be.a.String();
                    done();
                })
            })
        })
    })
})
